export function ArrowRightCircle({
  style,
}: {
  style: React.CSSProperties;
}): JSX.Element {
  return (
    <svg
      viewBox="0 0 60 60"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      // When converting to PDF, the SVG is getting ever so slightly
      // clipped, even though it looks fine in a browser. Letting that
      // sliver overflow seems to fix it.
      overflow="visible"
      style={style}
    >
      <circle cx="30" cy="30" r="30" fill="#000000" />
      <path d="M52.5 30L31.125 46.238L31.125 13.762L52.5 30Z" fill="white" />
      <rect x="9" y="24" width="22.5" height="12" fill="white" />
    </svg>
  );
}
